/**
 * 给定两个字符串 s 和 t，它们只包含小写字母。
 * 字符串 t 由字符串 s 随机重排，然后在随机位置添加一个字母。
 * 请找出在 t 中被添加的字母。
 *  
 * 示例 1：
 * 输入：s = "abcd", t = "abcde"
 * 输出："e"
 * 解释：'e' 是那个被添加的字母。
 * 
 * 示例 2：
 * 输入：s = "", t = "y"
 * 输出："y"
 * 
 * 示例 3：
 * 输入：s = "a", t = "aa"
 * 输出："a"
 * 
 * 示例 4：
 * 输入：s = "ae", t = "aea"
 * 输出："a"
 *  
 * 提示：
 * 0 <= s.length <= 1000
 * t.length == s.length + 1
 * s 和 t 只包含小写字母
 * 
 * @param {string} s
 * @param {string} t
 * @return {character}
 */
// 1. hash 计数
var findTheDifference = function(s, t) {
  const map = new Map();
  for(let i = 0; i < s.length; i++) {
    if(map.has(s[i])) {
      map.set(s[i], map.get(s[i]) + 1);
    } else {
      map.set(s[i], 1);
    }
  }
  for(let i = 0; i < t.length; i++) {
    if(map.has(t[i])) {
      const c = map.get(t[i]) - 1;
      if(c < 0) return t[i];
      map.set(t[i], c);
    } else {
      return t[i];
    }
  }
};

// 2. 数学求和
var findTheDifferenceII = function(s, t) {
  let res = 0;
  for(let i = 0; i < s.length; i++) {
    res += t.charCodeAt(i) - s.charCodeAt(i);
  }
  return String.fromCharCode(res + t.charCodeAt(t.length - 1));
}

// 3. 数学异或
var findTheDifferenceIII = function(s, t) {
  let res = 0;
  for(let i = 0; i < s.length; i++) {
    res ^= t.charCodeAt(i) ^ s.charCodeAt(i);
  }
  return String.fromCharCode(res ^ t.charCodeAt(t.length - 1));
}

const { logAssert } = require('./tools/LogTools.js');
const s = ""
const t = ""
logAssert(findTheDifferenceII, s, t, 't');